mapper.select:
	select 
    from cu_customer
     1 = 1

      	and name = #{customer.name,jdbcType=VARCHAR}


     	 	and mobile = #{customer.mobile,jdbcType=VARCHAR}


      	and sex_cd = #{customer.sexCd,jdbcType=VARCHAR}


      	and married_cd = #{customer.marriedCd,jdbcType=VARCHAR}


     		and certification_no = #{customer.certificationNo,jdbcType=VARCHAR}

    	and deleted_flag = 0

 			limit #{pageBean.offset},#{pageBean.size}

	select count(1)
    from cu_customer
     1 = 1

      	and name = #{name,jdbcType=VARCHAR}


     	 	and mobile = #{mobile,jdbcType=VARCHAR}


      	and sex_cd = #{sexCd,jdbcType=VARCHAR}


      	and married_cd = #{marriedCd,jdbcType=VARCHAR}


     		and certification_no = #{certificationNo,jdbcType=VARCHAR}

    	and deleted_flag = 0
