mapper.select:
select 

from mst_master

	deleted_flag = 0

		AND object_cd = #{master.objectCd,jdbcType=VARCHAR}


		AND name = #{master.name,jdbcType=VARCHAR}


		limit #{page.offset},#{page.size}

select a.object_cd objectCd, a.code, a.name, a.des, a.spell_no spellNo, a.sort_no sortNo, b.name objectNm
from mst_master a left join mst_object b on b.code = a.object_cd and b.deleted_flag= 0

	1 = 1

		AND a.object_cd = #{mstInVo.objectCd,jdbcType=VARCHAR}


		AND a.code = #{mstInVo.code,jdbcType=VARCHAR}


		AND (a.name like #{mstInVo.keyword,jdbcType=VARCHAR}"%" or a.spell_no like #{mstInVo.keyword,jdbcType=VARCHAR}"%")

	AND a.deleted_flag = 0

		limit #{page.offset},#{page.size}

select count(1) from mst_master a left join mst_object b on b.code = a.object_cd

	1 = 1

		AND a.object_cd = #{objectCd,jdbcType=VARCHAR}


		AND a.code = #{code,jdbcType=VARCHAR}


		AND (a.name like #{keyword,jdbcType=VARCHAR}"%" or a.spell_no like #{keyword,jdbcType=VARCHAR}"%")

	AND a.deleted_flag = 0

select count(1) from mst_master

	 object_cd = #{objectCd,jdbcType=VARCHAR}

		AND code = #{code,jdbcType=VARCHAR}


		AND name = #{name,jdbcType=VARCHAR}

	AND deleted_flag = 0

select object_cd objectCd, code, name, des, spell_no spellNo, sort_no sortNo
from mst_master
where object_cd = #{objectCd,jdbcType=VARCHAR}
  AND code = #{code,jdbcType=VARCHAR}
  AND deleted_flag = 0

select count(1) from mst_master

	 object_cd = #{objectCd,jdbcType=VARCHAR}
		AND name = #{name,jdbcType=VARCHAR}
		AND code != #{code,jdbcType=VARCHAR}
	AND deleted_flag = 0

mapper.update:
update mst_master set deleted_flag = 1
where code = #{code,jdbcType=VARCHAR} AND object_cd = #{objectCd,jdbcType=VARCHAR} 
AND deleted_flag = 0
