mapper.insert:

insert into ur_permission (code, name, uri, 
  version_no, created_time, created_by_cd, 
  last_updated_time, last_updated_by_cd, deleted_flag
  )
values (#{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{uri,jdbcType=VARCHAR}, 
  #{versionNo,jdbcType=INTEGER}, #{createdTime,jdbcType=TIMESTAMP}, #{createdByCd,jdbcType=VARCHAR}, 
  #{lastUpdatedTime,jdbcType=TIMESTAMP}, #{lastUpdatedByCd,jdbcType=VARCHAR}, #{deletedFlag,jdbcType=BIT}
  )

mapper.select:
select count(1)
from ur_permission
 deleted_flag = 0

		and name = #{permissionVo.name,jdbcType=VARCHAR}


		and code != #{permissionVo.code,jdbcType=VARCHAR}

select count(1)
from ur_permission
 deleted_flag = 0

		and en_name = #{permissionVo.enName,jdbcType=VARCHAR}


		and code != #{permissionVo.code,jdbcType=VARCHAR}
